<h2>Products</h2>
<div *ngFor="let product of products">
  <h3>
    <a
      [title]="product.name + ' details'"
      [routerLink]="['/products', product.id]"
    >
      {{ product.name }}
    </a>
  </h3>

   <img id="im" [src]="product.image" alt="">
<!--  <p *ngIf="product.description">Description: {{ product.description }}</p>-->

  <h4>{{ product.price | currency: 'KZT '}}</h4>

<!--  <button type="button" (click)="share()">Share</button>-->
  <button type="button"><a id="telegram" href="https://telegram.me/share/url?url={{product.link}}&text=Смотри-какой-классный-товар-я-нашел!">Share</a></button>

<!--  <app-product-alerts [product]="product" (notify)="onNotify()">-->
<!--  </app-product-alerts>-->
  <hr>
</div>


